import { Component } from '@angular/core';
import { LabSeqService } from '../services/LabSeqService';
import { Router } from '@angular/router';

@Component({
  selector: 'app-labseq',
  templateUrl: './LabSeqComponent.html', 
  styleUrls: ['LabSeqComponent.css'] 
})
export class LabSeqComponent {
  sequenceNumber: number = 0;
  labSeqValue: number = 0;

  constructor(private labSeqService: LabSeqService, private router: Router) {}

  getLabSeqValue(): void {
    this.labSeqService
      .getLabSeqValue(this.sequenceNumber)
      .subscribe((value) => (this.labSeqValue = value));
  }

  goToSwaggerUI(): void {
    this.router.navigate(['http://localhost:8080/q/swagger-ui']);
  }
}
<div class="form-container">
  <label for="sequenceNumber">Enter a sequence number:</label>
  <input type="number" id="sequenceNumber" [(ngModel)]="sequenceNumber" />
  <button (click)="getLabSeqValue()">Get LabSeq Value</button>
  <p *ngIf="labSeqValue !== undefined" class="result-text">
    LabSeq Value: {{ labSeqValue }}
  </p>
</div>
