// labseq.component.ts
import { Component } from '@angular/core';
import { LabSeqService } from '../services/LabSeqService';

@Component({
  selector: 'app-labseq',
  templateUrl: './LabSeqComponent.html', // Reference to the separate HTML file
})
export class LabSeqComponent {
  sequenceNumber: number = 0;
  labSeqValue: number = 0;

  constructor(private labSeqService: LabSeqService) {}

  getLabSeqValue(): void {
    this.labSeqService
      .getLabSeqValue(this.sequenceNumber)
      .subscribe((value) => (this.labSeqValue = value));
  }
}

<div>
