import { Component } from '@angular/core';
import { LabSeqService } from '../services/LabSeqService';
import { Subscription } from 'rxjs';

@Component({
  selector: 'app-labseq',
  templateUrl: './LabSeqComponent.html',
  styleUrls: ['LabSeqComponent.css'],
})
export class LabSeqComponent {
  sequenceNumber: string = ''; // Start with an empty string
  labSeqValue: string = '-'; // Start with an empty string
  loading: boolean = false;
  error: string | null = null;
  historyResults: { input: string; output: string }[] = [];
  private labSeqSubscription: Subscription | null = null;

  constructor(private labSeqService: LabSeqService) {}

  isValidInput(): boolean {
    // Use regex to match positive integers
    const positiveIntegerRegex = /^[0-9]\d*$/;
  
    // Check if the input is a positive integer and less than or equal to 1000
    const isValid = positiveIntegerRegex.test(this.sequenceNumber) && +this.sequenceNumber <= 1000;
  
    if (!isValid) {
      console.error('Invalid input. Please enter a positive integer less than or equal to 1000.');
    }
  
    return isValid;
  }

  getLabSeqValue(): void {
    this.loading = true;
    this.error = null;

    if (!this.isValidInput()) {
      return;
    }

    if (this.labSeqSubscription) {
      this.labSeqSubscription.unsubscribe();
    }

    // Convert the input to a number if it's not an empty string
    const inputNumber = this.sequenceNumber !== '' ? +this.sequenceNumber : 0;

    this.labSeqSubscription = this.labSeqService
      .getLabSeqValue(inputNumber)
      .subscribe({
        next: (value) => {
          this.labSeqValue = value.toString();
          this.historyResults.push({
            input: this.sequenceNumber?.toString() ?? 'N/A',
            output: this.labSeqValue.toString(),
          });
          this.sequenceNumber = ''; // Set the input to an empty string
          this.loading = false;
        },
        error: (error) => {
          console.error('Error fetching LabSeq value:', error);
          this.error = 'Error fetching LabSeq value. Please try again.';
          this.loading = false;
        },
      });
  }

  openSwaggerUI(): void {
    const swaggerUrl = 'http://localhost:8080/q/swagger-ui';
    window.open(swaggerUrl, '_blank');
  }
}

  <div class="intro-section">
    <h1 class="title">LabSeq Calculator!</h1>
    <p>
      This page allows you to explore the LabSeq sequence, a mathematical
      sequence defined by specific rules.
    </p>
    <div>
      <p>The LabSeq sequence is defined as follows:</p>
      <ul>
        <li>n=0 => l(0) = 0</li>
        <li>n=1 => l(1) = 1</li>
        <li>n=2 => l(2) = 0</li>
        <li>n=3 => l(3) = 1</li>
        <li>n>3 => l(n) = l(n-4) + l(n-3)</li>
      </ul>
    </div>
  </div>
  <div class="form-container" style="margin-top: 2rem">
    <label for="sequenceNumber">Enter value for {{'{n}'}}:</label>
    <div class="input-button-container">
      <input
        type="number"
        id="sequenceNumber"
        [(ngModel)]="sequenceNumber"
        placeholder="{n}"
      />
      <button (click)="getLabSeqValue()"  [disabled]="!isValidInput()">GET</button>
    </div>

    <div *ngIf="loading" class="loading-spinner">Fetching...</div>

    <div class="result-section" *ngIf="!loading && error === null">
      <p class="result-label">LabSeq Value:</p>
      <p class="result-text">{{ labSeqValue }}</p>
    </div>

    <div *ngIf="error !== null" class="error-section">
      <p class="error-text">{{ error }}</p>
    </div>

    <div class="swagger-section">
      <button (click)="openSwaggerUI()" class="swagger-button">
        Go to Swagger UI
      </button>
    </div>
  </div>

  <div class="history-card" style="margin-top: 2rem">
    <p class="history-label">History Results:</p>
    <table class="history-table">
      <thead>
        <tr>
          <th>Input</th>
          <th>Output LabSeq Value</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let result of historyResults" class="history-item">
          <td>{{ result.input }}</td>
          <td>{{ result.output }}</td>
        </tr>
      </tbody>
    </table>
  </div>

  <div class="github-link">
    <a
      href="https://github.com/dafilmorais"
      target="_blank"
      rel="noopener noreferrer"
    >
      Check out more of my projects {{'@'}}dafilmorais (GitHub)
    </a>
  </div>
