import { Component } from '@angular/core';
import { LabSeqService } from '../services/LabSeqService';
import { Router } from '@angular/router';

@Component({
  selector: 'app-labseq',
  templateUrl: './LabSeqComponent.html', 
  styleUrls: ['LabSeqComponent.css'] 
})
export class LabSeqComponent {
  sequenceNumber: number = 0;
  labSeqValue: number = 0;
  loading: boolean = false;
  constructor(private labSeqService: LabSeqService, private router: Router) {}

  getLabSeqValue(): void {
    this.loading = true; // Set loading to true before making the request
    this.labSeqService
      .getLabSeqValue(this.sequenceNumber)
      .subscribe(
        (value) => {
          this.labSeqValue = value;
          this.loading = false; // Set loading to false after receiving the response
        },
        (error) => {
          console.error('Error fetching LabSeq value:', error);
          this.loading = false; // Set loading to false in case of an error
        }
      );
  }

  openSwaggerUI(): void {
    // Replace 'your-swagger-url' with the actual Swagger UI URL
    const swaggerUrl = 'https://your-swagger-url';
    window.open(swaggerUrl, '_blank');
  }
}
