// LabSeqComponent.ts

import { Component } from '@angular/core';
import { LabSeqService } from '../services/LabSeqService';
import { Router } from '@angular/router';

@Component({
  selector: 'app-labseq',
  templateUrl: './LabSeqComponent.html', 
  styleUrls: ['LabSeqComponent.css'] 
})
export class LabSeqComponent {
  sequenceNumber: number = 0;
  labSeqValue: number = 0;
  loading: boolean = false;
  error: string | null = null; // New property to store the error message
  historyResults: { input: string, output: string }[] = [];

  constructor(private labSeqService: LabSeqService, private router: Router) {}

  getLabSeqValue(): void {
    this.loading = true;
    this.error = null;

    this.labSeqService
      .getLabSeqValue(this.sequenceNumber)
      .subscribe(
        (value) => {
          this.labSeqValue = value;
          this.historyResults.push({
            input: this.sequenceNumber.toString(),
            output: this.labSeqValue.toString()
          });
          this.loading = false;
        },
        (error) => {
          console.error('Error fetching LabSeq value:', error);
          this.error = 'Error fetching LabSeq value. Please try again.';
          this.loading = false;
        }
      );
  }

  openSwaggerUI(): void {
    const swaggerUrl = 'http://localhost:8080/q/swagger-ui';
    window.open(swaggerUrl, '_blank');
  }
}
<div class="main-container">
  <!-- First Card - LabSeq Value -->
  <div class="form-container" style="margin-top: 2rem;">
    <label for="sequenceNumber">Enter a sequence number:</label>
    <div class="input-button-container">
      <input type="number" id="sequenceNumber" [(ngModel)]="sequenceNumber" />
      <button (click)="getLabSeqValue()">GET</button>
    </div>

    <div *ngIf="loading" class="loading-spinner">
      Fetching...
    </div>

    <div class="result-section" *ngIf="!loading && error === null">
      <p class="result-label">LabSeq Value:</p>
      <p class="result-text">{{ labSeqValue }}</p>
    </div>

    <div *ngIf="error !== null" class="error-section">
      <p class="error-text">{{ error }}</p>
    </div>

    <div class="swagger-section">
      <button (click)="openSwaggerUI()" class="swagger-button">Go to Swagger UI</button>
    </div>
  </div>

<div class="history-card" style="margin-top: 2rem;">
  <p class="history-label">History Results:</p>
  <table class="history-table">
    <caption>LabSeq Value History</caption>
    <thead>
      <tr>
        <th>Input</th>
        <th>Output LabSeq Value</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let result of historyResults" class="history-item">
        <td>{{ result.input }}</td>
        <td>{{ result.output }}</td>
      </tr>
    </tbody>
  </table>
</div>
